// An arbitrary point in time at which all certificates are valid.
static constexpr base::Time kValidCertificateDate =
    base::Time::FromSecondsSinceUnixEpoch(1740614400);

// Returns the contents of the <certificate> tag from the sig XML.
std::string_view GetCertificate();

// Returns the contents of the <value> tag from the sig XML.
std::string_view GetSignature();

// Returns the contents of the intermediate certificates. These values are the
// same for both files.
